// Merge Sort Help: https://ide.geeksforgeeks.org/oIPSPOFqGY

export function getMergeSortAnimations(array) {
  // console.log("MERGING STARTS WITH ARRAY: ");
  // console.log(array);
  const animations = [];
  if (array.length <= 1) 
    return array;
  const auxiliaryArray = array.slice();
  mergeSortHelper(array, 0, array.length - 1, auxiliaryArray, animations);
  return animations;
}

function mergeSortHelper(
  mainArray,
  startIndex,
  endIndex,
  auxiliaryArray,
  animations,
) {
  if (startIndex === endIndex) 
    return;
  const middleIndex = Math.floor((startIndex + endIndex) / 2);
  mergeSortHelper(auxiliaryArray, startIndex, middleIndex, mainArray, animations);
  mergeSortHelper(auxiliaryArray, middleIndex + 1, endIndex, mainArray, animations);
  doMerge(mainArray, startIndex, middleIndex, endIndex, auxiliaryArray, animations);
}

function doMerge(
  mainArray,
  startIndex,
  middleIndex,
  endIndex,
  auxiliaryArray,
  animations,
) {
  let k = startIndex;
  let i = startIndex;
  let j = middleIndex + 1;
  // console.log([startIndex, middleIndex, endIndex]);
  while (i <= middleIndex && j <= endIndex) {
    // These are the values that we're comparing; we push them once
    // to change their color.
    // console.log("PUSHING IN ANIMATION: ");
    // console.log([i,  j]);
    // console.log([mainArray[i], mainArray[j]]);
    animations.push([i, j]);
    // These are the values that we're comparing; we push them a second
    // time to revert their color.
    // console.log("PUSHING IN ANIMATION: ");
    // console.log([i,  j]);
    // console.log([mainArray[i], mainArray[j]]);
    animations.push([i, j]);
    if (auxiliaryArray[i] <= auxiliaryArray[j]) {
      // We overwrite the value at index k in the original array with the
      // value at index i in the auxiliary array.
      // console.log("PUSHING IN ANIMATION: ");
      // console.log([k, auxiliaryArray[i]]);
      animations.push([k, auxiliaryArray[i]]);

      // console.log("Changing Values of mainArray with AuxiliaryArray: ");
      // console.log([mainArray[k], auxiliaryArray[i]]);
      mainArray[k++] = auxiliaryArray[i++];
    } else {
      // We overwrite the value at index k in the original array with the
      // value at index j in the auxiliary array.
      // console.log("PUSHING IN ANIMATION: ");
      // console.log([k, auxiliaryArray[j]]);
      animations.push([k, auxiliaryArray[j]]);
      
      // console.log("Changing Values of mainArray with AuxiliaryArray: ");
      // console.log([mainArray[k], auxiliaryArray[j]]);
      mainArray[k++] = auxiliaryArray[j++];
    }
  }
  while (i <= middleIndex) {
    // These are the values that we're comparing; we push them once
    // to change their color.
    console.log("PUSHING IN ANIMATION TWICE: ");
    console.log(i);
    console.log(mainArray[i]);
    animations.push([i, i]);
    // These are the values that we're comparing; we push them a second
    // time to revert their color.
    console.log("PUSHING IN ANIMATION TWICE: ");
    console.log(i);
    console.log(mainArray[i]);
    animations.push([i, i]);
    // We overwrite the value at index k in the original array with the
    // value at index i in the auxiliary array.
    console.log("PUSHING IN ANIMATION: ");
    console.log([k, auxiliaryArray[i]]);
    animations.push([k, auxiliaryArray[i]]);

    console.log("Changing Values of mainArray with AuxiliaryArray: ");
    console.log([mainArray[k], auxiliaryArray[i]]);
    mainArray[k++] = auxiliaryArray[i++];
  }
  while (j <= endIndex) {
    // These are the values that we're comparing; we push them once
    // to change their color.
    console.log("PUSHING IN ANIMATION TWICE: ");
    console.log(j);
    console.log(mainArray[j]);
    animations.push([j, j]);
    // These are the values that we're comparing; we push them a second
    // time to revert their color.
    console.log("PUSHING IN ANIMATION TWICE: ");
    console.log(j);
    console.log(mainArray[j]);
    animations.push([j, j]);
    // We overwrite the value at index k in the original array with the
    // value at index j in the auxiliary array.
    console.log("PUSHING IN ANIMATION: ");
    console.log([k, auxiliaryArray[j]]);
    animations.push([k, auxiliaryArray[j]]);
    
    console.log("Changing Values of mainArray with AuxiliaryArray: ");
    console.log([mainArray[k], auxiliaryArray[j]]);
    mainArray[k++] = auxiliaryArray[j++];
