export function getQuickSort(array) {
    // console.log(array);
    const auxiliaryArray = array.slice();
    const animations = [];
    sort(auxiliaryArray, 0, array.length);
    return auxiliaryArray;
}

function sort(auxiliaryArray, low, high) {
    if(low < high) {
        const pi = partition(auxiliaryArray, low, high);
        sort(auxiliaryArray, low, (pi - 1));
        sort(auxiliaryArray, (pi+1), high);
    }
}

function partition(auxiliaryArray, low, high) {
    const pivot = auxiliaryArray[low];
    animations.push(["Pivot", low]);
    var i = low;
    var temp;
    for(let j = low + 1; j <= high; j ++) {
        animations.push(["Compare", j])
        if(auxiliaryArray[j] <= pivot) {
            i = i + 1;
            temp = auxiliaryArray[i];
            auxiliaryArray[i] = auxiliaryArray[j];
            auxiliaryArray[j] = temp;
        }
    }
    temp = auxiliaryArray[i];
    auxiliaryArray[i] = auxiliaryArray[low];
    auxiliaryArray[low] = temp;
    return i;
}
