import React from 'react';
import * as Algorithms from '../Algorithms/mergeSort.js'
import './Visualizer.css';

export default class Visualizer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            array: [],
        };
    }

    componentDidMount() {
        this.resetArray();
    }

    resetArray() {
        const array = [];
        var max  = 500;
        var min = 5;
        for(let i = 0; i < 300; i ++) {
            array.push(Math.floor(Math.random() * max + min));
        }
        this.setState({array});
    }

    mergeSort() {
        const jsarray = this.state.array.slice().sort((a, b) => a - b);
        const sorted = Algorithms.mergeSort(this.state.array);
        console.log(ifEqual(jsarray, sorted));
    }

    quickSort() {}
    
    heapSort() {}
    
    bubbleSort() {}

    render() {
        const {array} = this.state;

        return (
            <div className = "array-container">
                {array.map((value, index) => (
                    <div className = "array-bar" key = {index} style = {{height: `${value}px`}}>
                    </div>
                ))}
                <button onClick = {() => this.resetArray()}>Randomize Array!</button>
                <button onClick = {() => this.mergeSort()}>Merge Sort</button>
                <button onClick = {() => this.quickSort()}>Quick Sort</button>
                <button onClick = {() => this.heapSort()}>HeapSort</button>
                <button onClick = {() => this.bubbleSort()}>Bubble Sort</button>
            </div>
        );
    }  
}

function ifEqual(arr1, arr2) {
    if (arr1.length != arr2.length) 
        return false;
    for (let i = 0; i < arr1.length; i ++){
        if(arr1[i] !== arr2[i])
        return false;
    }
    return true;
}
