import React from 'react';
import * as Merge from '../Algorithms/mergeSort.js'
import * as Bubble from '../Algorithms/bubbleSort.js'
// import * as Algorithms from '../Algorithms/bubbleSort.js'
import './Visualizer.css';

// Change this value for the speed of the animations.
const ANIMATION_SPEED_MS = 50;

// Change this value for the number of bars (value) in the array.
const NUMBER_OF_ARRAY_BARS = 50;

// This is the main color of the array bars.
const PRIMARY_COLOR = 'darkcyan';

// This is the color of array bars that are being compared throughout the animations.
const SECONDARY_COLOR = 'white';

export default class Visualizer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            array: [],
        };
    }

    componentDidMount() {
        this.resetArray();
    }

    resetArray() {
        const array = [];
        var max  = 500;
        var min = 5;
        for(let i = 0; i < NUMBER_OF_ARRAY_BARS; i ++) {
            array.push(Math.floor(Math.random() * max + min));
        }
        this.setState({array});
        console.log("Current Array: "+array);
        // const array1 = document.getElementsByClassName('array-bar');
        // array1.style.backgroundColor = "darkgrey";
    }

    mergeSort() {   
        const animations = Merge.getMergeSortAnimations(this.state.array);
        console.log("Final Animations: "+animations);
        for (let i = 0; i < animations.length; i++) {
            const arrayBars = document.getElementsByClassName('array-bar');
            const isColorChange = i % 3 !== 2;
            if (isColorChange) {
              const [barOneIndex, barTwoIndex] = animations[i];
              const barOneStyle = arrayBars[barOneIndex].style;
              const barTwoStyle = arrayBars[barTwoIndex].style;
              const color = i % 3 === 0 ? SECONDARY_COLOR : "Red";
              setTimeout(() => {
                barOneStyle.backgroundColor = color;
                barTwoStyle.backgroundColor = color;
              }, i * ANIMATION_SPEED_MS);
            } else {
              setTimeout(() => {
                const [barOneIndex, newHeight] = animations[i];
                const barOneStyle = arrayBars[barOneIndex].style;
                barOneStyle.height = `${newHeight}px`;
              }, i * ANIMATION_SPEED_MS);
            }
        }

    }

    quickSort() {}
    
    heapSort() {}
    
    bubbleSort() {
        const animations = Bubble.getBubbleSort(this.state.array);
        console.log(animations);
        const sorted = this.state.array.slice().sort((a, b) => a - b);
        for (let i = 0; i < animations.length; i++) {
            const arrayBars = document.getElementsByClassName('array-bar');
            const isColorChange = i % 3 !== 1;
            if (isColorChange) {
                const [barOneIndex, barTwoIndex] = animations[i];
                const barOneStyle = arrayBars[barOneIndex].style;
                const barTwoStyle = arrayBars[barTwoIndex].style;
                const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
                setTimeout(() => {
                    barOneStyle.backgroundColor = color;
                    barTwoStyle.backgroundColor = color;
                  }, i * ANIMATION_SPEED_MS);
            } else {
                setTimeout(() => {
                    const [newHeight1, newHeight2] = animations[i];
                    const [barOneIndex, barTwoIndex] = animations[i + 1];
                    const barOneStyle = arrayBars[barOneIndex].style;
                    const barTwoStyle = arrayBars[barTwoIndex].style;
                    barOneStyle.height = `${newHeight1}px`;
                    barTwoStyle.height = `${newHeight2}px`;
                  }, i * ANIMATION_SPEED_MS);
            }
        }
    }

    // testing() {
    //     var max  = -1000;
    //     var min = 1000;
    //     for (let i = 0; i < 100; i ++){
    //         const array = [];
    //         const len = Math.floor(Math.random() * max + 1);
    //         for (let j = 0; j < len; j ++){
    //             array.push(Math.floor(Math.random() * max + min));
    //         }
            // const jsarray = this.state.array.slice().sort((a, b) => a - b);
    //         const sorted = Algorithms.mergeSort(this.state.array);
    //         console.log(ifEqual(jsarray, sorted));
    //     }
    // }

    render() {
        const {array} = this.state;

        return (
            <div className = "array-container">
                {array.map((value, index) => (
                    <div className = "array-bar" key = {index} style = {{height: `${value}px`}}>
                    </div>
                ))}
                <div className = "button-container">
                    <button onClick = {() => this.resetArray()}>Randomize Array!</button>  
                    <button onClick = {() => this.mergeSort()}>Merge Sort</button>
                    <button onClick = {() => this.quickSort()}>Quick Sort</button>
                    <button onClick = {() => this.heapSort()}>HeapSort</button>
                    <button onClick = {() => this.bubbleSort()}>Bubble Sort</button>
                    <button onClick = {() => this.testing()}>Testing</button>
                </div>
            </div>    
            
        );
    }  
}

function ifEqual(arr1, arr2) {
    if (arr1.length != arr2.length) 
        return false;
    for (let i = 0; i < arr1.length; i ++){
        if(arr1[i] !== arr2[i])
        return false;
    }
    return true;
}
